import React, { useState } from "react";
import "./Navbar.css";
import logo from "../images/logoB.png";
import name from "../images/nameB.png";
import Modal from "react-modal";
import SignUp from "./SignUp";
import LogIn from "./LogIn";
import { useUser } from "reactfire";
import "firebase/auth";
import { IoIosClose } from "react-icons/io";
import Auth from "../helpers/Auth";

Modal.setAppElement("#root");

export default function Navbar() {
  const [signupIsOpen, setSignupIsOpen] = useState(false);
  const [loginIsOpen, setLoginIsOpen] = useState(false);

  const user = useUser();

  return (
    <div className="bar">
      <div className="brand">
        <img src={logo} alt="Logo" className="logo" />
        <img src={name} alt="Name" className="name" />
      </div>
      <div className="buttons">
        <button onClick={() => setLoginIsOpen(true)}>LOG IN</button>
        <Modal
          className="content"
          overlayClassName="overlay"
          isOpen={loginIsOpen}
          onRequestClose={() => setLoginIsOpen(false)}
        >
          <button className="close" onClick={() => setLoginIsOpen(false)}>
            <IoIosClose />
          </button>
          <LogIn />
          <div className="butdown">
            <button
              id="change"
              onClick={
                (() => setLoginIsOpen(false), () => setSignupIsOpen(true))
              }
            >
              Sign Up
            </button>
          </div>
          <Auth />
        </Modal>
        <button onClick={() => setSignupIsOpen(true)}>SIGN UP</button>
        <Modal
          className="content"
          overlayClassName="overlay"
          isOpen={signupIsOpen}
          onRequestClose={() => setSignupIsOpen(false)}
        >
          <button className="close" onClick={() => setSignupIsOpen(false)}>
            <IoIosClose />
          </button>
          <SignUp />
          <div className="butdown">
            <button
              id="change"
              onClick={
                (() => setSignupIsOpen(false), () => setLoginIsOpen(true))
              }
            >
              Log In
            </button>
          </div>
        </Modal>
      </div>
    </div>
  );
}
