import React, { useState } from "react";
import "./Navbar.css";
import logo from "../images/logoB.png";
import name from "../images/nameB.png";
import Modal from "react-modal";
import SignUp from "./SignUp";
import LogIn from "./LogIn";
import { useFirebaseApp, useUser } from "reactfire";
import "firebase/auth";
import { IoIosClose } from "react-icons/io";

Modal.setAppElement("#root");

export default function Navbar() {
  const [signupIsOpen, setSignupIsOpen] = useState(false);
  const [loginIsOpen, setLoginIsOpen] = useState(false);

  const firebase = useFirebaseApp();
  const user = useUser();

  const logout = async () => {
    await firebase.auth().signOut();
  };

  function closeSignUp() {
    setSignupIsOpen(false);
  }
  function openSignUp() {
    setSignupIsOpen(true);
  }
  function closeLogIn() {
    setSignupIsOpen(false);
  }
  function openLogIn() {
    setSignupIsOpen(true);
  }

  return (
    <div className="bar">
      <div className="brand">
        <img src={logo} alt="Logo" className="logo" />
        <img src={name} alt="Name" className="name" />
      </div>
      <div className="buttons">
        {!user && (
          <div>
            <button onClick={openLogIn}>LOG IN</button>
            <Modal
              className="content"
              overlayClassName="overlay"
              isOpen={loginIsOpen}
              onRequestClose={closeLogIn}
            >
              <button className="close" onClick={closeLogIn}>
                <IoIosClose />
              </button>
              <LogIn />
              <div className="butdown">
                <button
                  id="change"
                  onClick={() => {
                    closeLogIn();
                    openSignUp();
                  }}
                >
                  > Sign Up
                </button>
              </div>
            </Modal>
            <button onClick={openSignUp}>SIGN UP</button>
            <Modal
              className="content"
              overlayClassName="overlay"
              isOpen={signupIsOpen}
              onRequestClose={closeSignUp}
            >
              <button className="close" onClick={closeSignUp}>
                <IoIosClose />
              </button>
              <SignUp />
              <div className="butdown">
                <button id="change" onClick={closeSignUp}>
                  Log In
                </button>
              </div>
            </Modal>
          </div>
        )}
        {user && (
          <div>
            <button onClick={logout}>LOG OUT</button>
            <button id="fLetter">{user.email.charAt(0)}</button>
          </div>
        )}
      </div>
    </div>
  );
}
