import React, { useState } from "react";
import "./Navbar.css";
import logo from "../images/logoB.png";
import name from "../images/nameB.png";
import Modal from "react-modal";
import SignUp from "./SignUp";
import LogIn from "./LogIn";
import { useFirebaseApp, useUser } from "reactfire";
import "firebase/auth";
import { IoIosClose } from "react-icons/io";
import { IoMdPersonAdd } from "react-icons/io";
import { FiUserCheck } from "react-icons/fi";

Modal.setAppElement("#root");

export default function Navbar() {
  const [signupIsOpen, setSignupIsOpen] = useState(false);
  const [loginIsOpen, setLoginIsOpen] = useState(false);

  const firebase = useFirebaseApp();
  const user = useUser();

  const logout = async () => {
    await firebase.auth().signOut();
  };

  function closeSignUp() {
    setSignupIsOpen(false);
  }
  function openSignUp() {
    setSignupIsOpen(true);
  }
  function closeLogIn() {
    setLoginIsOpen(false);
  }
  function openLogIn() {
    setLoginIsOpen(true);
  }

  return (
    <div className="bar">
      <div className="brand">
        <img src={logo} alt="Logo" className="logo" />
        <img src={name} alt="Name" className="name" />
      </div>
      <div className="buttons">
        {!user && (
          <div>
            <button className="textbut" onClick={openLogIn}>
              LOG IN
            </button>
            <button className="iconbut" onClick={openLogIn}>
              <FiUserCheck />
            </button>
            <Modal
              className="content"
              overlayClassName="overlay"
              isOpen={loginIsOpen}
              onRequestClose={closeLogIn}
            >
              <button className="close" onClick={closeLogIn}>
                <IoIosClose />
              </button>
              <LogIn />
              <div className="butdown">
                <button
                  id="change"
                  onClick={() => {
                    closeLogIn();
                    openSignUp();
                  }}
                >
                  Sign Up
                </button>
              </div>
            </Modal>
            <button className="textbut" onClick={openSignUp}>
              SIGN UP
            </button>
            <button className="iconbut" onClick={openLogIn}>
              <IoMdPersonAdd />
            </button>
            <Modal
              className="content"
              overlayClassName="overlay"
              isOpen={signupIsOpen}
              onRequestClose={closeSignUp}
            >
              <button className="close" onClick={closeSignUp}>
                <IoIosClose />
              </button>
              <SignUp />
              <div className="butdown">
                <button
                  id="change"
                  onClick={() => {
                    closeSignUp();
                    openLogIn();
                  }}
                >
                  Log In
                </button>
              </div>
            </Modal>
          </div>
        )}
        {user && (
          <div>
            <button onClick={logout}>LOG OUT</button>
            <button id="fLetter">{user.email.charAt(0)}</button>
          </div>
        )}
      </div>
    </div>
  );
}
